MainWindow::MainWindow(QWidget *parent)
    : QMainWindow(parent), ui(new Ui::MainWindow)
{
    ui->setupUi(this);
    resize(1200, 608);

    scene = new QGraphicsScene(this);
    crearEscena();

    scene->setSceneRect(0, 0, 1200, 608);

    QGraphicsView *view = new QGraphicsView(scene, this);
    view->setFixedSize(1205, 613);
    view->setBackgroundBrush(QBrush(QColor("black")));
    setCentralWidget(view);
}
void MainWindow::crearEscena() {
    for (int i = 0; i < ALTO_MAPA; ++i) {
        for (int j = 0; j < ANCHO_MAPA; ++j) {
            int tileType = mapa[i][j];
            QGraphicsRectItem *tile = nullptr;

            if (tileType == 1) { // Muro
                tile = scene->addRect(j * TILE_SIZE, i * TILE_SIZE, TILE_SIZE, TILE_SIZE, QPen(Qt::black), QBrush(Qt::black));
            } else if (tileType == 0) { // Caminable
                tile = scene->addRect(j * TILE_SIZE, i * TILE_SIZE, TILE_SIZE, TILE_SIZE, QPen(Qt::black), QBrush(Qt::gray));
            } else if (tileType == 2) { // Puerta
                tile = scene->addRect(j * TILE_SIZE, i * TILE_SIZE, TILE_SIZE, TILE_SIZE, QPen(Qt::black), QBrush(Qt::red));
            }
        }
    }
}

MainWindow::~MainWindow()
{
    delete ui;
}
